SELECT * FROM ( ( SELECT ROWNUM rn, t.* FROM ( SELECT distinct incident.DEAL, incident.INCIDENT_ID, incident.APPDATE, incident.USER_NO, incident.SOURCE, to_char(incident.APPDATE,'yyyy-mm-dd hh24:mi:ss') AS APPDATE_STRING, to_char(incident.APPDATE + numtodsinterval( b.PROCCESS_TIME, 'hour' ),'yyyy-mm-dd') AS expireString, incident.APPLY_NO, incident.STATUS, ( SELECT ITEM_VALUE FROM SYS_DICT_ITEM WHERE incident.STATUS = ITEM_CODE AND DICT_CODE = 'IncidentStatus' ) AS STATUS_DICT, incident.SHW_COMPANY, ( SELECT NAME FROM MGT_WATER_COMPANY WHERE COMPANY_CODE = incident.SHW_COMPANY ) AS COMPANY_NAME, incident.SHW_ADDRESS, incident.INCIDENT_TYPE, incident.CERT_TYPE, ( SELECT ITEM_VALUE FROM SYS_DICT_ITEM WHERE incident.CERT_TYPE = ITEM_CODE AND DICT_CODE = 'CertificateType' ) AS CERT_TYPE_DICT, incident.APPLICANT, incident.CERT_NUMBER, incident.YWXT_SUCCESS, incident.YWTB_SUCCESS, incident.IF_RESULT, to_char(incident.UDATE,'yyyy-mm-dd hh24:mi:ss') AS UPDATE_TIME, incident.APPDATE + numtodsinterval( b.PROCCESS_TIME, 'hour' ) AS EXPIRE_DATE, (CASE WHEN incident.STATUS = 'IncidentStatus.Finish' OR incident.STATUS = 'IncidentStatus.Return' OR incident.STATUS = 'IncidentStatus.Defer' THEN 0 ELSE ( CASE WHEN incident.APPDATE + numtodsinterval( b.PROCCESS_TIME, 'hour' ) - SYSDATE > 2 THEN 0 WHEN incident.APPDATE + numtodsinterval( b.PROCCESS_TIME, 'hour' ) - SYSDATE > 0 AND incident.APPDATE + numtodsinterval( b.PROCCESS_TIME, 'hour' ) - SYSDATE <= 2 THEN 1 ELSE 2 END ) END ) AS EXPIRE_STATUS, info.HOUSE_CERT_TYPE, (SELECT ITEM_VALUE FROM SYS_DICT_ITEM WHERE info.HOUSE_CERT_TYPE = ITEM_CODE AND DICT_CODE = 'HouseCertType') AS HOURSE_CERT_DICT, info.HOUSE_CERT_NO, info.CONTACT_NUM, info.EMAIL, info.BDCZL, info.BDCDBRQ, info.BOOK_TIME
FROM MCS_INCIDENT incident, USW_OPEN_METER info, MCS_ITEM_CODE b WHERE incident.INCIDENT_ID = info.INCIDENT_ID AND incident.INCIDENT_TYPE = b.INCIDENT_TYPE AND incident.INCIDENT_TYPE = #{incidentType,jdbcType=VARCHAR}
AND incident.STATUS = #{status,jdbcType=VARCHAR}
AND ((incident.STATUS = 'IncidentStatus.Finish' OR incident.STATUS = 'IncidentStatus.Return' OR incident.STATUS = 'IncidentStatus.Defer') OR incident.APPDATE + numtodsinterval( b.PROCCESS_TIME, 'hour' ) - SYSDATE > 2)
AND incident.STATUS != 'IncidentStatus.Finish' AND incident.STATUS != 'IncidentStatus.Return' AND incident.STATUS != 'IncidentStatus.Defer' AND incident.APPDATE + numtodsinterval( b.PROCCESS_TIME, 'hour' ) - SYSDATE > 0 AND incident.APPDATE + numtodsinterval( b.PROCCESS_TIME, 'hour' ) - SYSDATE <= 2
AND incident.STATUS != 'IncidentStatus.Finish' AND incident.STATUS != 'IncidentStatus.Return' AND incident.STATUS != 'IncidentStatus.Defer' AND incident.APPDATE + numtodsinterval( b.PROCCESS_TIME, 'hour' ) - SYSDATE <= 0
AND incident.SHW_COMPANY = #{companyCode,jdbcType=VARCHAR}
AND incident.USER_NO = #{userNo,jdbcType=VARCHAR}
AND incident.APPDATE <= TO_DATE(#{endTime,jdbcType=VARCHAR},'yyyy-MM-dd hh24:mi:ss')
AND incident.APPDATE >= TO_DATE(#{startTime,jdbcType=VARCHAR},'yyyy-MM-dd hh24:mi:ss')
AND incident.APPLICANT LIKE #{name}
AND info.CONTACT_NUM LIKE #{phone}
AND incident.DEAL = #{deal,jdbcType=VARCHAR}
ORDER BY incident.APPDATE DESC ) t WHERE ROWNUM <= #{page,jdbcType=VARCHAR} * #{limit,jdbcType=VARCHAR} )) rowpage WHERE rn > #{page,jdbcType=VARCHAR} * #{limit,jdbcType=VARCHAR} - #{limit,jdbcType=VARCHAR}

SELECT COUNT(1) FROM MCS_INCIDENT incident, USW_OPEN_METER info,MCS_ITEM_CODE b WHERE info.INCIDENT_ID = incident.INCIDENT_ID AND incident.INCIDENT_TYPE = b.INCIDENT_TYPE AND incident.INCIDENT_TYPE = #{incidentType,jdbcType=VARCHAR}
AND incident.STATUS = #{status,jdbcType=VARCHAR}
AND ((incident.STATUS = 'IncidentStatus.Finish' OR incident.STATUS = 'IncidentStatus.Return' OR incident.STATUS = 'IncidentStatus.Defer') OR incident.APPDATE + numtodsinterval( b.PROCCESS_TIME, 'hour' ) - SYSDATE > 2)
AND incident.STATUS != 'IncidentStatus.Finish' AND incident.STATUS != 'IncidentStatus.Return' AND incident.STATUS != 'IncidentStatus.Defer' AND incident.APPDATE + numtodsinterval( b.PROCCESS_TIME, 'hour' ) - SYSDATE > 0 AND incident.APPDATE + numtodsinterval( b.PROCCESS_TIME, 'hour' ) - SYSDATE <= 2
AND incident.STATUS != 'IncidentStatus.Finish' AND incident.STATUS != 'IncidentStatus.Return' AND incident.STATUS != 'IncidentStatus.Defer' AND incident.APPDATE + numtodsinterval( b.PROCCESS_TIME, 'hour' ) - SYSDATE <= 0
AND incident.SHW_COMPANY = #{companyCode,jdbcType=VARCHAR}
AND incident.USER_NO = #{userNo,jdbcType=VARCHAR}
AND incident.APPDATE <= TO_DATE(#{endTime,jdbcType=VARCHAR},'yyyy-MM-dd hh24:mi:ss')
AND incident.APPDATE >= TO_DATE(#{startTime,jdbcType=VARCHAR},'yyyy-MM-dd hh24:mi:ss')
AND incident.APPLICANT LIKE #{name}
AND info.CONTACT_NUM LIKE #{phone}
AND incident.DEAL = #{deal,jdbcType=VARCHAR}

SELECT incident.DEAL, incident.INCIDENT_ID, incident.APPDATE, incident.USER_NO, incident.SOURCE, incident.COMPANY_NAME AS APPLY_COMPANY_NAME, incident.APPLY_NO, incident.STATUS, ( SELECT ITEM_VALUE FROM SYS_DICT_ITEM WHERE incident.STATUS = ITEM_CODE AND DICT_CODE = 'IncidentStatus' ) AS STATUS_DICT, incident.SHW_COMPANY, ( SELECT NAME FROM MGT_WATER_COMPANY WHERE COMPANY_CODE = incident.SHW_COMPANY ) AS COMPANY_NAME, incident.SHW_ADDRESS, incident.INCIDENT_TYPE, incident.CERT_TYPE, incident.IF_RESULT, to_char(incident.UDATE,'yyyy-mm-dd hh24:mi:ss') AS UPDATE_TIME, incident.APPDATE + numtodsinterval( b.PROCCESS_TIME, 'hour' ) AS EXPIRE_DATE, (CASE WHEN incident.STATUS = 'IncidentStatus.Finish' OR incident.STATUS = 'IncidentStatus.Return' OR incident.STATUS = 'IncidentStatus.Defer' THEN 0 ELSE ( CASE WHEN incident.APPDATE + numtodsinterval( b.PROCCESS_TIME, 'hour' ) - SYSDATE > 2 THEN 0 WHEN incident.APPDATE + numtodsinterval( b.PROCCESS_TIME, 'hour' ) - SYSDATE > 0 AND incident.APPDATE + numtodsinterval( b.PROCCESS_TIME, 'hour' ) - SYSDATE <= 2 THEN 1 ELSE 2 END ) END ) AS EXPIRE_STATUS, ( SELECT ITEM_VALUE FROM SYS_DICT_ITEM WHERE incident.CERT_TYPE = ITEM_CODE AND DICT_CODE = 'CertificateType' ) AS CERT_TYPE_DICT, incident.APPLICANT, incident.CERT_NUMBER, incident.YWXT_SUCCESS, incident.YWTB_SUCCESS, info.CONTACT_VALUE, info.ADDRESSEE, info.ADD_PHONE, info.MAIL_ADDRESS, info.POST_CODE, info.BILL_DATE FROM MCS_INCIDENT incident, USW_PAY_REMAIL_APPLY info, MCS_ITEM_CODE b WHERE incident.INCIDENT_ID = info.INCIDENT_ID AND incident.INCIDENT_TYPE = #{incidentType,jdbcType=VARCHAR} AND incident.INCIDENT_TYPE = b.INCIDENT_TYPE
AND incident.YWXT_SUCCESS = 1
AND incident.YWXT_SUCCESS = 1
AND incident.STATUS = #{status,jdbcType=VARCHAR}
AND incident.SHW_COMPANY IN
#{item}
AND incident.SHW_COMPANY = #{companyCode,jdbcType=VARCHAR}
AND incident.APPDATE <= TO_DATE(#{enddate,jdbcType=VARCHAR},'yyyy-MM-dd hh24:mi:ss')
AND incident.APPDATE >= TO_DATE(#{startdate,jdbcType=VARCHAR},'yyyy-MM-dd hh24:mi:ss')
